import tw from "tailwind-styled-components";
import vincenzo from '../../res/images/vincenzo.jpg';
import oliwia from '../../res/images/oliwia.jpg';
import React from "react";
import styled from 'styled-components';

const Card = tw.div`
  flex
  justify-center
`;

const Container = tw.div`
  flex
  flex-col 
  md:flex-row 
  md:max-w-xl 
  rounded-lg
  bg-white shadow-lg
`;

const Image = styled.div`
  background-image: url(${props => props.url});
  width: 100%;
  height: 24rem;
  @media (min-width: 768px) {
    .md\:h-auto {
        height: auto;
    }
  }
  object-fit: cover;
  @media (min-width: 768px) {
    .md\:w-48 {
        width: 12rem/* 192px */;
    }
  }
  border-top-left-radius: 0.5rem/* 8px */;
  border-top-right-radius: 0.5rem/* 8px */;
  .rounded-t-lg {
    border-top-left-radius: 0.5rem/* 8px */;
    border-top-right-radius: 0.5rem/* 8px */;
  }
  @media (min-width: 768px) {
    .md\:rounded-l-lg {
        border-top-left-radius: 0.5rem/* 8px */;
        border-bottom-left-radius: 0.5rem/* 8px */;
    }
  }
`;

const ImageWrapper = tw.div`
  bg-[url(../../res/images/oliwia.jpg)]
  w-full 
  h-96
  md:h-auto 
  object-cover 
  md:w-48 
  rounded-t-lg 
  md:rounded-none 
  md:rounded-l-lg
`

const ColumnContainer = tw.div`
  p-6 
  flex 
  flex-col 
  justify-start
`

const NameText = tw.text`
  text-gray-900 
  text-xl 
  font-medium 
  mb-2
`

export default function About() {
  return (
    <Card>
      <Container>
        <Image url={oliwia}></Image>
          <ColumnContainer>
            <NameText>Oliwia Stanislawek</NameText>
              <p className="text-gray-700 text-base mb-4">
              Nam vitae fringilla magna. Integer lobortis lectus ligula, vitae dignissim dui malesuada nec. Maecenas posuere, tortor mattis vestibulum pretium, risus ligula finibus erat, sit amet tempus nunc lacus ut metus. Phasellus sed mattis dui.
              </p>
            <p className="text-gray-600 text-xs">Last updated 3 mins ago</p>
          </ColumnContainer>
      </Container>
    </Card>
  );
}
